#include "base/win/scoped_hstring.h"

#include <delayimp.h>

#include "base/strings/string_piece.h"
#include "base/strings/utf_string_conversions.h"
#include "base/threading/thread_restrictions.h"

namespace {

// A frame-based exception handler filter function for a handler for exceptions
// generated by the Visual C++ delay loader helper function.
int FilterVisualCPPExceptions(DWORD exception_code) {
  return HRESULT_FACILITY(exception_code) == FACILITY_VISUALCPP
             ? EXCEPTION_EXECUTE_HANDLER
             : EXCEPTION_CONTINUE_SEARCH;
}

}  // namespace

namespace base {
namespace win {

// static
ScopedHString ScopedHString::Create(const base::char16* str) {
  HSTRING hstr;
  HRESULT hr =
      WindowsCreateString(str, static_cast<uint32_t>(wcslen(str)), &hstr);
  if (SUCCEEDED(hr))
    return ScopedHString(hstr);
  DLOG(ERROR) << "Failed to create HSTRING" << std::hex << hr;
  return ScopedHString(nullptr);
}

// static
ScopedHString ScopedHString::Create(HSTRING hstr) {
  return ScopedHString(hstr);
}

ScopedHString::ScopedHString(HSTRING hstr) : ScopedGeneric(nullptr) {
  reset(hstr);
}

// static
bool ScopedHString::PreloadRequiredFunctions() {
  ThreadRestrictions::AssertIOAllowed();

  static const bool load_succeeded = []() {
    HRESULT hr = E_FAIL;
    __try {
      hr = __HrLoadAllImportsForDll("api-ms-win-core-winrt-string-l1-1-0.dll");
    } __except (FilterVisualCPPExceptions(::GetExceptionCode())) {
      hr = E_FAIL;
    }
    return SUCCEEDED(hr);
  }();
  return load_succeeded;
}

std::string ScopedHString::GetString() {
  // Note: empty HSTRINGs are represent as nullptr, and instantiating
  // std::string with nullptr (in base::WideToUTF8) is undefined behavior.
  uint32_t length;
  const base::char16* buffer = WindowsGetStringRawBuffer(get(), &length);
  if (!buffer)
    return nullptr;
  std::string value;
  if (!base::WideToUTF8(buffer, length, &value))
    return nullptr;
  return value;
}

base::StringPiece16 ScopedHString::GetString16() {
  return WindowsGetStringRawBuffer(get(), nullptr);
}

}  // namespace win
}  // namespace base
